import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import { msmeAPI, nbfcAPI } from '../api';

export default function MSMEApplications() {
  const { user, logout } = useAuth();
  const [data, setData] = useState(null);
  const [nbfcs, setNbfcs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showNewAppModal, setShowNewAppModal] = useState(false);
  const [selectedNbfcs, setSelectedNbfcs] = useState([]);
  const [formData, setFormData] = useState({
    amount: '',
    tenure: '12',
    purpose: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      try {
        const dashboardData = await msmeAPI.getDashboard();
        const nbfcData = await nbfcAPI.getAll();
        setData(dashboardData);
        setNbfcs(nbfcData);
      } catch (error) {
        console.error('Failed to fetch data:', error);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleNbfc = (nbfcId) => {
    if (selectedNbfcs.includes(nbfcId)) {
      setSelectedNbfcs(selectedNbfcs.filter(id => id !== nbfcId));
    } else {
      if (selectedNbfcs.length < 3) {
        setSelectedNbfcs([...selectedNbfcs, nbfcId]);
      } else {
        alert('You can select up to 3 NBFCs only');
      }
    }
  };

  const handleSubmitApplication = (e) => {
    e.preventDefault();
    if (selectedNbfcs.length === 0) {
      alert('Please select at least one NBFC');
      return;
    }
    alert(`Application submitted to ${selectedNbfcs.length} NBFC(s)! (Demo mode)`);
    setShowNewAppModal(false);
    setFormData({ amount: '', tenure: '12', purpose: '' });
    setSelectedNbfcs([]);
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl text-slate-600">Loading...</div>
      </div>
    );
  }

  const menuItems = [
    { name: 'Dashboard', path: '/msme/dashboard', icon: 'ðŸ“Š' },
    { name: 'Profile', path: '/msme/profile', icon: 'ðŸ‘¤' },
    { name: 'Applications', path: '/msme/applications', icon: 'ðŸ“„' },
    { name: 'Local Enterprise Group', path: '/msme/leg', icon: 'ðŸ‘¥' },
    { name: 'Active Loans', path: '/msme/loans', icon: 'ðŸ’°' }
  ];

  return (
    <div className="min-h-screen bg-slate-50 flex">
      {/* Sidebar */}
      <aside className="w-64 bg-teal-800 text-white flex-shrink-0">
        <div className="p-6">
          <div className="flex items-center gap-3 mb-8">
            <div className="p-2 bg-white rounded-lg">
              <svg className="h-6 w-6 text-teal-800" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
              </svg>
            </div>
            <div>
              <div className="font-bold">MSME Portal</div>
              <div className="text-xs text-teal-200">J&K MSME System</div>
            </div>
          </div>

          <nav className="space-y-2">
            {menuItems.map((item, i) => (
              <Link
                key={i}
                to={item.path}
                className={`flex items-center gap-3 px-4 py-3 rounded-lg transition-colors ${
                  window.location.pathname === item.path
                    ? 'bg-teal-900 text-white'
                    : 'text-teal-100 hover:bg-teal-700'
                }`}
              >
                <span className="text-xl">{item.icon}</span>
                <span className="text-sm font-medium">{item.name}</span>
              </Link>
            ))}
          </nav>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Header */}
        <header className="bg-white border-b border-gray-200 px-8 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-lg font-semibold text-slate-900">MSME Credit Access System</h1>
              <p className="text-xs text-slate-500">Jammu & Kashmir</p>
            </div>
            <div className="flex items-center gap-4">
              <button className="text-slate-600 hover:text-slate-900">
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3.055 11H5a2 2 0 012 2v1a2 2 0 002 2 2 2 0 012 2v2.945M8 3.935V5.5A2.5 2.5 0 0010.5 8h.5a2 2 0 012 2 2 2 0 104 0 2 2 0 012-2h1.064M15 20.488V18a2 2 0 012-2h3.064M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </button>
              <span className="text-sm text-slate-600">English</span>
              
              <button className="text-slate-600 hover:text-slate-900">
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
                </svg>
              </button>

              <div className="relative">
                <button
                  onClick={() => setShowUserMenu(!showUserMenu)}
                  className="flex items-center gap-3 pl-3 pr-4 py-2 bg-slate-50 rounded-lg hover:bg-slate-100"
                >
                  <div className="h-8 w-8 rounded-full bg-teal-600 flex items-center justify-center text-white font-semibold">
                    G
                  </div>
                  <div className="text-left">
                    <div className="text-sm font-semibold text-slate-900">{user.businessName || user.name}</div>
                    <div className="text-xs text-white bg-teal-600 px-2 py-0.5 rounded-full inline-block">MSME</div>
                  </div>
                </button>

                {showUserMenu && (
                  <div className="absolute right-0 mt-2 w-64 bg-white rounded-lg shadow-lg border border-gray-200 py-2 z-50">
                    <div className="px-4 py-3 border-b border-gray-200">
                      <div className="font-semibold text-slate-900">{user.businessName || user.name}</div>
                      <div className="text-sm text-slate-600">{user.email}</div>
                    </div>
                    <Link
                      to="/msme/profile"
                      className="flex items-center gap-2 px-4 py-2 hover:bg-slate-50 text-slate-700"
                    >
                      <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                      </svg>
                      Profile
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="flex items-center gap-2 px-4 py-2 hover:bg-red-50 text-red-600 w-full text-left"
                    >
                      <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                      </svg>
                      Logout
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </header>

        {/* Applications Content */}
        <main className="flex-1 p-8 overflow-y-auto">
          <div className="flex items-center justify-between mb-6">
            <div>
              <h2 className="text-2xl font-bold text-slate-900">Credit Applications</h2>
              <p className="text-slate-600">Manage your loan applications</p>
            </div>
            <button
              onClick={() => setShowNewAppModal(true)}
              className="px-4 py-2 bg-slate-900 text-white rounded-lg hover:bg-slate-800 inline-flex items-center gap-2"
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4" />
              </svg>
              New Application
            </button>
          </div>

          {/* Threshold Info */}
          <div className="bg-blue-50 border border-blue-200 rounded-xl p-4 mb-6 flex items-start gap-3">
            <svg className="h-5 w-5 text-blue-600 flex-shrink-0 mt-0.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <p className="text-sm text-slate-700">
              <span className="font-semibold">Credit Threshold for {user.district}:</span> Individual applications up to â‚¹{(data?.districtThreshold || 0).toLocaleString()}. Amounts above require group (LEG) application.
            </p>
          </div>

          {/* Search & Filter */}
          <div className="flex items-center gap-4 mb-6">
            <div className="flex-1 relative">
              <svg className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-slate-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
              <input
                type="text"
                placeholder="Search applications..."
                className="w-full pl-10 pr-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-slate-900 focus:border-transparent"
              />
            </div>
            <select className="px-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-slate-900 focus:border-transparent">
              <option>All Status</option>
              <option>Approved</option>
              <option>Pending</option>
              <option>Disbursed</option>
            </select>
          </div>

          {/* Applications List */}
          <div className="space-y-4">
            {(data?.recent_applications || []).map((app, i) => (
              <div key={i} className="bg-white border border-slate-200 rounded-xl p-6 shadow-sm">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <h3 className="text-2xl font-bold text-slate-900">â‚¹{app.amount.toLocaleString()}</h3>
                      <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                        app.status === 'Approved' ? 'bg-emerald-100 text-emerald-700' :
                        app.status === 'Disbursed' ? 'bg-purple-100 text-purple-700' :
                        'bg-amber-100 text-amber-700'
                      }`}>
                        {app.status}
                      </span>
                      {app.isGroup && (
                        <span className="px-3 py-1 bg-blue-100 text-blue-700 rounded-full text-xs font-medium">
                          Group
                        </span>
                      )}
                    </div>
                    <p className="text-slate-600 mb-1">{app.purpose}</p>
                    <div className="flex items-center gap-4 text-sm text-slate-500">
                      <span>24 months</span>
                      <span>â€¢</span>
                      <span>Risk Score: 79.2</span>
                      <span>â€¢</span>
                      <span>{app.date}</span>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm text-slate-500 mb-1">Responses</div>
                    <div className="text-xl font-bold text-slate-900">{app.responses} NBFC(s)</div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </main>
      </div>

      {/* New Application Modal */}
      {showNewAppModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b border-slate-200 px-6 py-4 flex items-center justify-between">
              <h3 className="text-xl font-bold text-slate-900">New Credit Application</h3>
              <button
                onClick={() => setShowNewAppModal(false)}
                className="text-slate-400 hover:text-slate-600"
              >
                <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            <form onSubmit={handleSubmitApplication} className="p-6 space-y-6">
              <p className="text-slate-600">Fill in the details to apply for credit</p>

              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-slate-700 mb-2">Loan Amount (â‚¹) *</label>
                  <input
                    type="number"
                    value={formData.amount}
                    onChange={(e) => setFormData({...formData, amount: e.target.value})}
                    required
                    placeholder="e.g., 500000"
                    className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-slate-900 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-slate-700 mb-2">Tenure (Months) *</label>
                  <select
                    value={formData.tenure}
                    onChange={(e) => setFormData({...formData, tenure: e.target.value})}
                    className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-slate-900 focus:border-transparent"
                  >
                    <option value="6">6 months</option>
                    <option value="12">12 months</option>
                    <option value="18">18 months</option>
                    <option value="24">24 months</option>
                    <option value="36">36 months</option>
                  </select>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">Purpose *</label>
                <textarea
                  value={formData.purpose}
                  onChange={(e) => setFormData({...formData, purpose: e.target.value})}
                  required
                  rows="3"
                  placeholder="Describe the purpose of this loan"
                  className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-slate-900 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-700 mb-3">Select NBFCs (up to 3) *</label>
                <div className="space-y-3 max-h-64 overflow-y-auto">
                  {nbfcs.map((nbfc) => (
                    <label
                      key={nbfc.id}
                      className={`flex items-start gap-3 p-4 border-2 rounded-lg cursor-pointer transition-all ${
                        selectedNbfcs.includes(nbfc.id)
                          ? 'border-slate-900 bg-slate-50'
                          : 'border-slate-200 hover:border-slate-300'
                      }`}
                    >
                      <input
                        type="checkbox"
                        checked={selectedNbfcs.includes(nbfc.id)}
                        onChange={() => toggleNbfc(nbfc.id)}
                        className="mt-1"
                      />
                      <div className="flex-1">
                        <div className="font-semibold text-slate-900 mb-1">{nbfc.name}</div>
                        <div className="text-sm text-slate-600">
                          Rate: {nbfc.rate?.split('/')[0]} | Min: â‚¹{nbfc.minAmount?.toLocaleString()} | Max: â‚¹{nbfc.maxAmount?.toLocaleString()}
                        </div>
                      </div>
                    </label>
                  ))}
                </div>
                <p className="text-xs text-slate-500 mt-2">Selected: {selectedNbfcs.length}/3</p>
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="button"
                  onClick={() => setShowNewAppModal(false)}
                  className="flex-1 px-6 py-3 border border-slate-300 text-slate-700 rounded-lg hover:bg-slate-50 font-medium"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="flex-1 px-6 py-3 bg-slate-900 text-white rounded-lg hover:bg-slate-800 font-medium"
                >
                  Submit Application
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
